'use strict';

var compilerDom = require('@vue/compiler-dom');

var Morph_ssr = (dir) => {
  const name = dir.arg
    ? '\'' + dir.arg.content.split(':')[ 0 ] + '\''
    : false;

  return {
    props: [
      compilerDom.createObjectProperty(
        'class',
        `${ dir.exp.content } === ${ name } ? '' : 'q-morph--invisible'`
      )
    ]
  }
};

module.exports = Morph_ssr;
